// app/profile/page.js
import { cookies } from "next/headers";
import { Image } from "antd";
import styles from "./profile.module.css";
import LogoutButton from "./LogoutButton";
import AuthForm from "./AuthForm";
import { redirect } from 'next/navigation';

import LocalStorageUpdater from "../components/LocalStorageUpdater";

async function fetchUserData(token) {
  try {
    const response = await fetch("https://stylish.yunweidai.net/api/1.0/user/profile", {
      headers: {
        Authorization: `Bearer ${token}`,
        cache: 'no-store' 
      },
    });
    if (response.status === 401) { // Unauthorized, token expired
      console.log("token invalid");
      return { invalid: true };
    }
    if (!response.ok) {
      throw new Error("Failed to fetch user data");
    }
    const data = await response.json();
    return { data: data.data, invalid: false };
  } catch (error) {
    console.error("Error fetching user data:", error);
    return { invalid: true };
  }
}

export default async function ProfilePage() {
  const cookieStore = cookies();
  const cookieList = cookieStore.getAll();
  const jwtCookie = cookieList.find((cookie) => cookie.name === "jwt");
 
  let jwtToken = jwtCookie ? jwtCookie.value : null;

  if (!jwtToken) {
    return <AuthForm />;
  }


  const { data: userData, invalid } = await fetchUserData(jwtToken);

  if (invalid) {
      //TODO reset cookie jwt
  
  }

    return (
      <div>
       <LocalStorageUpdater jwt={jwtToken} />
        {userData ? (
          <div className={styles.profile}>
            <div className={styles.profile_title}>用戶資訊</div>
            <div className={styles.profile_content}>
              <Image
                width={200}
                src = {userData.picture}
                fallback="data:image/png;base64,"
              />
              <div className={styles.user_info}>
                <div className={styles.name}>姓名: {userData.name}</div>
                <div className={styles.email}>郵件: {userData.email}</div>
                <div className={styles.options_container}>
                  <a className={styles.options}>更換頭貼</a>
                  <LogoutButton></LogoutButton>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    );
  }

